mapper.select:
SELECT B.RN, B.SHARE_ID, B.TITLE, B.CONTENT, B.VIEW_COUNT, B.CREATE_ID, B.UPDATE_ID, B.CREATE_DT, B.UPDATE_DT, B.DEL_YN FROM( SELECT ROWNUM AS RN, A.SHARE_ID, A.TITLE, A.CONTENT, A.VIEW_COUNT, A.CREATE_ID, A.UPDATE_ID, TO_CHAR(A.CREATE_DT,'YYYY-MM-DD') AS CREATE_DT, TO_CHAR(A.UPDATE_DT,'YYYY-MM-DD') AS UPDATE_DT, A.DEL_YN FROM SHARE_BOARD A
AND A.DEL_YN = 'N'
AND ( A.TITLE LIKE '%'||#{searchText}||'%' OR A.CONTENT LIKE '%'||#{searchText}||'%' )
AND A.CREATE_DT >= TO_DATE(#{startDate}, 'YYYY-MM-DD'
AND A.CREATE_DT <= TO_DATE(#{endDate}, 'YYYY-MM-DD'
)B WHERE B.RN BETWEEN #{startRow} AND #{endRow}

mapper.update:
UPDATE SHARE_BOARD 
SET DEL_YN = 'Y',  
    UPDATE_ID = #{updateId}  
WHERE SHARE_ID = #{shareId}
